import React from "react";
import "./nav.scss";

const Nav = ({ list, isMobile }) => {
    function liClick(e) {
        e.preventDefault();
        var activeLi;
    }
    return (
        <nav className="menu">
            <ul className="menu-ul menu__menu-ul">
                {list.map((li, index) => (
                    <li
                        key={index}
                        data-index={index + 2}
                        className={`menu-ul__li menu-ul__li-${li.src}`}
                        onClick={liClick}
                    >
                        <span />
                        <a href={`#${li.src}`}>
                            {i18n.t(`dataHeader.navtitle.${index}.title`)}
                        </a>
                    </li>
                ))}
            </ul>
        </nav>
    );
};

export default Nav;
