import React, { Component } from "react";
import "./about.scss";
import "./pagination.scss";
import about from "../../images/group.jpg";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const About = ({slider_list}) => {
  var settings = {
    arrows: false,
    autoplay: true,
    dots: true,
    infinite: true,
    speed: 600,
    dotsClass: "pagination"
  };
  return (
    <section className="section">
      <div className="about">
        <div className="about__image">
          <img src={about} alt="Uma.Tech" />
        </div>
        <div className="about__right">
          <div className="about__header">
            <h2 className="typography typography--header">
              <span>| </span>О нас
            </h2>
          </div>
          <Slider {...settings} className="about__text">
            <div>
              <h3 className="typography typography--headersm">
                Мы&nbsp;&mdash; высокотехнологичная компания с&nbsp;многолетним
                опытом работы на&nbsp;рынке&nbsp;ИТ и&nbsp;телекоммуникаций.
              </h3>
              <p className="typography typography--text">
                Создавая комплексные решения для медиа-индустрии, мы построили
                одну из крупнейших федеральных сетей доставки видеоконтента, ряд
                уникальных технических решений и отработали гибкие бизнес-схемы,
                что позволяет нам удовлетворять запросы любого заказчика.
              </p>
            </div>
          </Slider>
        </div>
      </div>
    </section>
  );
};

export default About;
